mapper.select:
select * 
from (
	select
		 POINT_KEY, EMAIL, JUMO_POINT, RULLETDATE, R
	from (
		select
			 POINT_KEY, EMAIL, JUMO_POINT, RULLETDATE, ROWNUM R
		from 
			JUMO_POINT
		where 
			EMAIL = #{EMAIL}
		order by RULLETDATE DESC
	)
	order by R ASC
)

select * 
from (
	select
		 POINT_KEY, EMAIL, JUMO_POINT, RULLETDATE, R
	from (
		select
			 POINT_KEY, EMAIL, JUMO_POINT, RULLETDATE, ROWNUM R
		from 
			JUMO_POINT
		where 
			EMAIL = #{EMAIL}
			and
			RULLETDATE BETWEEN #{MINDATE} AND #{MAXDATE}
		order by RULLETDATE DESC
	)
	order by R ASC
)

mapper.update:
update
	JUMO_EVENT
set
	JUMO_POINT = #{JUMO_POINT}
where 
	EMAIL = #{EMAIL}
