mapper.select:

	SELECT * 
	FROM 
		PRODUCT 


	
	
		AND PKIND = #{PKIND} 
	
	
		AND PDEGREE BETWEEN #{PDEGREEMIN} AND #{PDEGREEMAX} 
	
    
		AND PPRICE BETWEEN #{PPRICEMIN} AND #{PPRICEMAX} 
	


	
		PSELL DESC  
	
             
		PPRICE ASC 
             
             
		PPRICE DESC 
             
             
		PDATE DESC


	SELECT * 
		FROM PRODUCT 



	
		AND PKIND = #{PKIND} 
	
	
		AND PDEGREE BETWEEN #{PDEGREEMIN} AND #{PDEGREEMAX} 
	
              
		AND PPRICE BETWEEN #{PPRICEMIN} AND #{PPRICEMAX} 
	
		AND PTYPE='ALCOHOL' 


    
		PSELL DESC 
	
    
		PPRICE ASC 
    
    
		PPRICE DESC 
    
    
		PDATE DESC


	SELECT *
	FROM (
		SELECT PID, PNAME, PIMAGE, PPRICE, PSALE, ROWNUM R
		FROM ( 	
			SELECT PID, PNAME, PIMAGE, PPRICE, PSALE
			FROM 
				PRODUCT 
			
		 	
				
				
					AND PKIND = #{PKIND} 
				
				
				
					AND PDEGREE BETWEEN #{PDEGREEMIN} AND #{PDEGREEMAX} 
				
				
			    
					AND PPRICE BETWEEN #{PPRICEMIN} AND #{PPRICEMAX} 
				
			
			
		 		
					PSELL DESC  
				
				
					PPRICE ASC 
				
				
					PPRICE DESC 
				
				
					PDATE DESC 
				
			
		)
		ORDER BY R
	)
	WHERE
		R BETWEEN #{START} AND #{END}


	SELECT *
	FROM (
		SELECT PID, PNAME, PIMAGE, PPRICE, PSALE, ROWNUM R
		FROM ( 	
			SELECT PID, PNAME, PIMAGE, PPRICE, PSALE
			FROM 
				PRODUCT 
			
		 	
				
				
					AND PKIND = #{PKIND} 
				
				
				
					AND PDEGREE BETWEEN #{PDEGREEMIN} AND #{PDEGREEMAX} 
				
				
			    
					AND PPRICE BETWEEN #{PPRICEMIN} AND #{PPRICEMAX} 
				
					AND PTYPE ='ALCOHOL'
			
			
		 		
					PSELL DESC  
				
				
					PPRICE ASC 
				
				
					PPRICE DESC 
				
				
					PDATE DESC 
				
			
		)
		ORDER BY R
	)
	WHERE
		R BETWEEN #{START} AND #{END}

